sysauto_script.script:
// Get today's time and date
	var rightNow = new GlideDateTime();
// Get the date and time 24 hours from now
	//var futureDate = new GlideDateTime("2019-04-15 16:17:00");
	//futureDate.addDaysLocalTime(1);

	// Query the database for NeedIt Task records with Due date field values between now and 24 hours from now.
	// Only return NeedIt Task records that do not have
	// a State field value of Closed Complete.
	var duesoonNITasks = new GlideRecord('x_58872_needit_needit_task');
	//duesoonNITasks.addQuery('due_date','>=',rightNow);
	//duesoonNITasks.addQuery('due_date','<',futureDate);
	duesoonNITasks.addQuery('due_date','<',gs.hoursAgo(-24));
	duesoonNITasks.addQuery('state','!=',3);

	duesoonNITasks.query();
	// Write a log message for each due soon NeedIt Task Record
	while(duesoonNITasks.next()){
		gs.info("NeedIt Task records due soon = " + duesoonNITasks.number);

		gs.eventQueue('x_58872_needit.dueSoonNeedItTasks',duesoonNITasks,duesoonNITasks.number,gs.getUserName());
	}
